package javaconcepts;

public class Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Decrement operator
		for (int i=5;i>0;i--) {
		System.out.println(i);

	}
		//Increment operator

		for (int i=0;i<5;i++) {
			System.out.println(i);
		}
	
		
		//while loop
		
		int i=0;
		while(i<=5) {
			System.out.println(i);
			i++;
		}
		
		//pre Increment
		
		
		
		//Post Increment
		
		//Pre Decrement
		
		//Post Decrement
}
}